{"path":"/Users/ravirawat/Desktop/cb/ApiAgents/src/services/mathService.ts","content":"// This file will define services for math operations

export const addNumbers = (a: number, b: number): number => {
  return a + b
}

export const subtractNumbers = (a: number, b: number): number => {
  return a - b
}

export const multiplyNumbers = (a: number, b: number): number => {
  return a * b
}

export const divideNumbers = (a: number, b: number): number => {
  return a / b
}

export const modulusNumbers = (a: number, b: number): number => {
  return a % b
}

export const powerNumbers = (a: number, b: number): number => {
  return Math.pow(a, b)
}

export const averageNumbers = (a: number, b: number): number => {
  return (a + b) / 2
}

export const squareRootNumber = (a: number): number => {
  return Math.sqrt(a)
}
"}

