{"path": "/Users/ravirawat/Desktop/cb/ApiAgents/src/controllers/divideController.ts", "content": "import { Handler } from 'hono';

export const divideController: Handler = (c) => {
  const a = Number(c.req.query('a'));
  const b = Number(c.req.query('b'));
  if (b === 0) {
    return c.json({ error: 'Cannot divide by zero' }, 400);
  }
  return c.json({ result: a / b });
};
"}

