{"path":"/Users/ravirawat/Desktop/cb/ApiAgents/src/router.ts","content":"import express from 'express';

const router = express.Router();

router.post('/add', (req, res) => {
  const { a, b } = req.body;
  res.json(a + b);
});

router.post('/subtract', (req, res) => {
  const { a, b } = req.body;
  res.json(a - b);
});

router.post('/multiply', (req, res) => {
  const { a, b } = req.body;
  res.json(a * b);
});

router.post('/divide', (req, res) => {
  const { a, b } = req.body;
  if (b !== 0) {
    res.json(a / b);
  } else {
    res.status(400).json({ error: 'Cannot divide by zero' });
  }
});

router.post('/modulus', (req, res) => {
  const { a, b } = req.body;
  res.json(a % b);
});

router.post('/power', (req, res) => {
  const { a, b } = req.body;
  res.json(Math.pow(a, b));
});

router.post('/average', (req, res) => {
  const { a, b } = req.body;
  res.json((a + b) / 2);
});

router.post('/square-root', (req, res) => {
  const { a } = req.body;
  res.json(Math.sqrt(a));
});

export default router;"}

