{"path":"/Users/ravirawat/Desktop/cb/ApiAgents/src/controllers/mathController.ts","content":"import { Request, Response } from 'express';

export const add = (req: Request, res: Response) => {
    const { a, b } = req.body;
    res.status(200).json(a + b);
};

export const subtract = (req: Request, res: Response) => {
    const { a, b } = req.body;
    res.status(200).json(a - b);
};

export const multiply = (req: Request, res: Response) => {
    const { a, b } = req.body;
    res.status(200).json(a * b);
};

export const divide = (req: Request, res: Response) => {
    const { a, b } = req.body;
    if (b === 0) return res.status(400).json({ error: 'Division by zero.' });
    res.status(200).json(a / b);
};

export const modulus = (req: Request, res: Response) => {
    const { a, b } = req.body;
    res.status(200).json(a % b);
};

export const power = (req: Request, res: Response) => {
    const { a, b } = req.body;
    res.status(200).json(Math.pow(a, b));
};

export const average = (req: Request, res: Response) => {
    const { a, b } = req.body;
    res.status(200).json((a + b) / 2);
};

export const squareRoot = (req: Request, res: Response) => {
    const { a } = req.body;
    if (a < 0) return res.status(400).json({ error: 'Square root of negative number.' });
    res.status(200).json(Math.sqrt(a));
};
"}

