{"path":"/Users/ravirawat/Desktop/cb/ApiAgents/src/middleware/authMiddleware.ts","content":"import { Request, Response, NextFunction } from 'express';

export const basicAuth = (req: Request, res: Response, next: NextFunction) => {
    const authHeader = req.headers.authorization;
    if (!authHeader || authHeader.split(' ')[0] !== 'Basic') {
        return res.status(401).json({ error: 'Unauthorized' });
    }

    const base64Credentials = authHeader.split(' ')[1];
    const credentials = Buffer.from(base64Credentials, 'base64').toString('ascii');
    const [username, password] = credentials.split(':');

    // Simple check; in real case, validate with your DB or other secure methods
    if (username === 'admin' && password === 'password') {
        return next();
    }

    return res.status(401).json({ error: 'Unauthorized' });
};"}

